set @questid = 7794;
UPDATE quests_localized SET Objectives		= 'Unsere Vorräte an Magiestoff gehen zur Neige und wir könnten Eure Hilfe gebrauchen, um unsere Bestände wieder aufzufüllen! Wenn wir uns auf die Gemeinschaft im Ganzen verlassen, bräuchten wir lediglich eine Spende von 60 Stücken Magiestoff von Euch, um unser Ziel umsetzen zu können. Ich versichere Euch, dass eine derartige Großzügigkeit in Sturmwind nicht unbeachtet bleiben würde!' WHERE entry=@questid  AND language_code='deDE';
UPDATE quests_localized SET IncompleteText 	= 'Wenn Ihr 60 Stück des Magiestoffes habt und bereit seit, diesen zu spenden, würde ich diese Spende jederzeit gerne annehmen.' WHERE entry=@questid AND language_code='deDE';

