UPDATE quests_localized SET Objectives		= 'Verwendet Schnappbolzens Luftanalysegerät, um die Luft zu untersuchen, die 6 stark parfümierte Wachen umgibt, und kehrt zu Detektiv Snap Schnappbolz in Orgrimmar zurück. ' WHERE entry=@questid AND language_code='deDE';
