set @questid = 24850;
REPLACE INTO quests_localized (entry, Title, language_code) VALUES (@questid, 'Flennis Flamme', 'deDE');
UPDATE quests_localized SET Objectives		= 'Redet mit Roka bei Gotris Reiseausstattung in der Gasse in Orgrimmar.' WHERE entry=@questid AND language_code='deDE';
UPDATE quests_localized SET Details			= '$GJunge:Mädel;, hier geht es um mehr als bloß Parfüm. In dieser Kiste waren ein paar Raketen und sie sind mit irgend einer chemischen Ladung gefüllt. Hier kann ich sie nicht analysieren, aber ich werde Euch den Weg ins Labor ersparen.$b$bIhr müsst nur den Typ aufspüren, der sie hergestellt hat. Ich kenne das Zeichen des Herstellers. Flenni Rostrakete.$b$bIch habe gehört, dass er es sich hier in Orgrimmar gut gehen lässt. Und es kommt noch besser, er soll sogar eine Freundin haben... eine Orc-Dame mit Namen Roka. Sie will sich mit ihm bei Gotris Reiseausstattung in der Gasse treffen, bevor sie ihre Flucht antreten. ' WHERE entry=@questid AND language_code='deDE';
UPDATE quests_localized SET CompletionText	= 'Ihr seid nicht Flenni. Er hätte schon vor einer Stunde hier sein sollen! Was habt Ihr mit ihm angestellt?$b$b<Roka scheint sich zu beruhigen, als Ihr ihr erklärt, dass Ihr selber nach Flenni sucht. Dann schimmern Tränen in ihren Augen.>$b$bDas sieht meinem süßen Flenni gar nicht ähnlich. Meint Ihr, ihm ist etwas zugestoßen?' WHERE entry=@questid AND language_code='deDE';

